mapper.delete:
DELETE FROM group_user_info
WHERE group_id = #{groupId}
  AND user_id = #{userId}

mapper.select:
SELECT user_id, group_id, role_id, requested_at, status
FROM group_role_request
WHERE group_id=#{groupId}

mapper.update:
  	UPDATE group_role_request
	SET
		status=

				'ACCEPTED'
				'REJECTED'

	WHERE 
		user_id=#{userId} 
		AND group_id=#{groupId} 
		AND role_id=#{roleId}
		AND status='PENDING'
