mapper.select:
SELECT post_id, title, content, views, created_at, author, IFNULL(pl.likes, 0) AS likes, category_id, category_name,
CASE WHEN EXISTS ( SELECT 1 FROM post_like pl2 WHERE pl2.post_id = p.post_id AND pl2.user_id = #{userId} ) THEN true ELSE false END AS user_liked
FROM post p LEFT JOIN( SELECT post_id, COUNT(*) AS likes FROM post_like GROUP BY post_id )AS pl USING(post_id) JOIN post_category USING (category_id) WHERE post_id=#{postId}

SELECT comment_id, post_id, content, writer, created_at
FROM comment
WHERE post_id=#{postId}
