mapper.update:
UPDATE member_expense_info
SET expense_to_pay = #{budget}
WHERE group_id=#{groupId}

UPDATE member_expense_info
SET paid_amount = paid_amount + #{paidAmount}
WHERE user_id = #{userId} AND group_id = #{groupId}

mapper.select:
SELECT SUM(paid_amount)
FROM member_expense_info
WHERE group_id=#{groupId}
