mapper.select:
SELECT id, email, pw, nickname, gender, age, role, activated 
FROM user
WHERE email = #{email}

SELECT id, email, pw, nickname, gender, age, role, activated
FROM user
WHERE id = #{userId}

mapper.update:
UPDATE user
SET pw = #{newPassword}
WHERE id = #{id}

UPDATE user
SET activated=1
WHERE id=#{userId}

    	UPDATE user
		SET activated=0
		WHERE id=#{userId}

mapper.select:
SELECT EXISTS (
	SELECT 1 FROM user 
	WHERE email = #{email}
)

SELECT EXISTS (
	SELECT 1 FROM user 
	WHERE id = #{userId}
